import { Component } from '@angular/core';
import {Observable, switchMap} from 'rxjs';
import {Country, State} from './types';
import {CountryService} from './country.service';
import {FormControl} from '@angular/forms';

@Component({
  selector: 'app-solution2',
  templateUrl: './solution2.component.html',
  styleUrls: ['./solution2.component.css']
})
export class Solution2Component {

  countries$: Observable<Country[]> = this.service.getCountries();
  states$: Observable<State[]>;
  countryDropdown = new FormControl<Country['id']>(null);
  statesDropdown = new FormControl<State['code']>(null);

  constructor(private service: CountryService) {
    this.states$ = this.countryDropdown.valueChanges.pipe(
      switchMap(countryId => service.getStatesFor(countryId))
    )
  }
}
<h2>Solution 2</h2>
<h2>Result</h2>
<div *ngIf="countries$ | async as countries">
  <div>Select one of {{countries.length}} countries:</div>
  <select [formControl]="countryDropdown">
    <option *ngFor="let country of countries" [value]="country.id">
      {{country.description}}
    </option>
  </select>
  <div>Current value: {{countryDropdown.value}}</div>
</div>
<div *ngIf="states$ | async as states">
  <ng-container *ngIf="states.length > 0">
    <div>Select one of {{states.length}} states/provinces:</div>
    <select [formControl]="statesDropdown" [disabled]="states.length == 0">
      <option value="">Select...</option>
      <option *ngFor="let state of states" [value]="state.code">
        {{state.description | titlecase}}
      </option>
    </select>
    <div>Current value: {{statesDropdown?.value}}</div>
  </ng-container>
</div>
