import { Component } from '@angular/core';
import {timer} from 'rxjs';

@Component({
  selector: 'app-solution6',
  templateUrl: './solution6.component.html',
  styleUrls: ['./solution6.component.css']
})
export class Solution6Component {

  action$ = timer(2000);


}
<h2>Solution 6</h2>
<h2>Result</h2>
<div >
   <button [action]="action$" defaultText="Save" textWhenWorking="Saving..." textWhenDone="Saved!"></button>
</div>
<br> <br>
<div>
   <a href="javascript:none()" [action]="action$" defaultText="Save from a link" textWhenWorking="Saving..." textWhenDone="Saved!"></a>
</div>
